YUI.add("tag-ybind",function(a){a.Tag.register("ybind",{created:function(b){var c=this.get("host");if(b.on==="valuechange"){c=c.one("input, textarea");}c.on(b.on,function(d){a.fire("target:"+b.name,d.target.get("value"));});if(b.init){setTimeout(function(){a.fire("target:"+b.name,b.init);},0);}}});a.Tag.register("ytarget",{created:function(b){a.on("target:"+b.name,function(c){this.get("host").setHTML(c);},this);}});},"@VERSION@",{requires:["gallery-tag","event"]});